INSERT INTO `events` (`event_id`, `character_id`, `event_name`, `description`, `heart_level`, `created_at`, `updated_at`) VALUES
(3, 48, 'Two Hearts', '&lt;p&gt;Upon reaching Two Hearts with March, he will send the player a letter with the Heart Quest for Surprise Me,&lt;/p&gt;\r\n&lt;p&gt;asking for the player to meet with him at the Blacksmith&#039;s Shop.&lt;/p&gt;\r\n&lt;p&gt;[Player],&lt;/p&gt;\r\n&lt;p&gt;Meet me at the Blacksmith&#039;s, and don&#039;t keep me waiting.&lt;/p&gt;\r\n&lt;p&gt;March&lt;/p&gt;\r\n&lt;p&gt;Meeting March at the Blacksmith&#039;s Shop will complete the Heart Quest.&lt;/p&gt;', '2', '2024-12-05 23:35:38', '2024-12-05 23:41:02'),
(4, 48, 'Four Hearts', '&lt;p&gt;Upon reaching Four Hearts with March,&amp;nbsp;Olric will send the player a letter with the Heart Quest for Many Hands Make Light Work, asking for the player to meet with him at the forge by the Blacksmith&#039;s Shop.&lt;/p&gt;\r\n&lt;p&gt;Hey [Player]!&lt;/p&gt;\r\n&lt;p&gt;Are you busy? March and I sure are! We can hardly keep up with all our work.&lt;/p&gt;\r\n&lt;p&gt;March told me your blacksmithing has been improving, and that I shouldn&#039;t tell you he said that. Buuuut what I think he meant to say was &quot;I sure hope [Ari] can help us out!&quot;&lt;/p&gt;\r\n&lt;p&gt;Hope to see you at the forge soon!&lt;/p&gt;\r\n&lt;p&gt;Your pal,&lt;/p&gt;\r\n&lt;p&gt;Olric&lt;/p&gt;\r\n&lt;p&gt;&lt;strong&gt;Details&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p&gt;After reaching Four Hearts with March, he will send you four&lt;strong&gt; Iron Ingot&lt;/strong&gt; on your birthday every year.&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;', '4', '2024-12-05 23:42:04', '2024-12-05 23:44:45'),
(6, 43, 'Two Hearts', '&lt;p&gt;Upon reaching Two Hearts with Eiland, he will send the player a letter with the Heart Quest for The Stele, asking for the player to meet with him outside the Manor House.&lt;/p&gt;\r\n&lt;p&gt;[Player],&lt;/p&gt;\r\n&lt;p&gt;I&#039;m sure by now you&#039;ve seen that Mistria is full of ancient places, with many more left to discover.&lt;/p&gt;\r\n&lt;p&gt;If you&#039;re interested, meet me at the Manor House and I can show you a particularly interesting site on the grounds!&lt;/p&gt;\r\n&lt;p&gt;Eiland&lt;/p&gt;\r\n&lt;p&gt;Meeting with Eiland will complete the Heart Quest.&lt;/p&gt;', '2', '2024-12-05 23:51:10', '2024-12-06 01:28:10'),
(7, 43, 'Four Hearts', '&lt;p&gt;Upon reaching four hearts with Eiland, he will send the player a letter with the Heart Quest for To the Ruins!, asking for the player to meet him at the Western Ruins.&lt;/p&gt;\r\n&lt;p&gt;[Player],&lt;/p&gt;\r\n&lt;p&gt;The new text you uncovered has been an enormous help regarding the whereabouts of the Dragonsworn Armor.&lt;/p&gt;\r\n&lt;p&gt;I have every reason to believe that we can discover more at the Western Ruins.&lt;/p&gt;\r\n&lt;p&gt;Meet me there and we can begin!&lt;/p&gt;\r\n&lt;p&gt;Eiland&lt;/p&gt;\r\n&lt;p&gt;Meeting with Eiland will complete the Heart Quest.&lt;/p&gt;\r\n&lt;p&gt;&lt;strong&gt;Details&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p&gt;After reaching Four Hearts with Eiland, he will send you a Chocolate Cake on your birthday every year.&lt;/p&gt;', '4', '2024-12-06 00:00:11', '2024-12-06 00:00:11'),
(8, 47, 'Two Hearts', '&lt;p&gt;Upon reaching Two Hearts with Celine, she will send the player a letter with the&lt;/p&gt;\r\n&lt;p&gt;Heart Quest for The Unusual Seed, asking for the player to meet with her at&amp;nbsp;Celine&#039;s Cottage.&lt;/p&gt;\r\n&lt;p&gt;[Player],&lt;/p&gt;\r\n&lt;p&gt;Could you come to my house when you have a moment?&lt;/p&gt;\r\n&lt;p&gt;I&#039;ve got a question about a flower that I hope you can help with!&lt;/p&gt;\r\n&lt;p&gt;Celine&lt;/p&gt;\r\n&lt;p&gt;Meeting Celine in her cottage will complete the Heart Quest.&lt;/p&gt;', '2', '2024-12-06 00:01:02', '2024-12-06 01:32:38'),
(9, 47, 'Four Hearts', '&lt;p&gt;Upon reaching four hearts with Celine, she will send the player a letter with the Heart Quest for Water and Soil, asking for the player to meet her at Celine&#039;s Cottage.&lt;/p&gt;\r\n&lt;p&gt;[Player],&lt;/p&gt;\r\n&lt;p&gt;The seed we found still hasn&#039;t sprouted...&lt;/p&gt;\r\n&lt;p&gt;I&#039;ve been doing research into it and I&#039;m starting to think its growth conditions might be very particular.&lt;/p&gt;\r\n&lt;p&gt;Could you come over and lend me your insight as a farmer as well? It&#039;d be very appreciated!&lt;/p&gt;\r\n&lt;p&gt;Celine&lt;/p&gt;\r\n&lt;p&gt;Meeting Celine at her cottage will complete the Heart Quest.&lt;/p&gt;\r\n&lt;p&gt;&lt;strong&gt;Details &lt;/strong&gt;&lt;/p&gt;\r\n&lt;p&gt;After reaching Four Hearts with&amp;nbsp;Celine, she will send you a Rose Tea on your birthday every year.&lt;/p&gt;', '4', '2024-12-06 00:01:29', '2024-12-06 00:01:29'),
(10, 51, 'Two Hearts', '&lt;p&gt;Upon reaching Two Hearts with Juniper, she will post the&amp;nbsp;Heart Quest,&amp;nbsp;Becoming Juniper&#039;s Guinea Pig, on the message board advertising that she&#039;s giving away free health tonics at the&amp;nbsp;Bathhouse.&lt;/p&gt;\r\n&lt;p&gt;Come and get your free health tonic at Juniper&#039;s Bathhouse!&lt;/p&gt;\r\n&lt;p&gt;Clears your pores, freshens your breath, and side effects are minimal.&lt;/p&gt;\r\n&lt;p&gt;Available while supplies last.&lt;/p&gt;\r\n&lt;p&gt;Meeting Juniper at the Bathhouse will complete the Heart Quest.&lt;/p&gt;', '2', '2024-12-06 00:02:08', '2024-12-06 00:02:08'),
(11, 51, 'Four Hearts', '&lt;p&gt;Upon reaching Four Hearts with Juniper, she will send the player a letter with the Heart Quest for Horsing Around, asking the player to meet her at the Bathhouse.&lt;/p&gt;\r\n&lt;p&gt;[Player],&lt;/p&gt;\r\n&lt;p&gt;I&#039;ve prepared a potion to test on you, so come to the bathhouse.&lt;/p&gt;\r\n&lt;p&gt;Don&#039;t make me wait too long, or I might just test it on someone else.&lt;/p&gt;\r\n&lt;p&gt;Juniper&lt;/p&gt;\r\n&lt;p&gt;Meeting Juniper at the Bathhouse will complete the Heart Quest.&lt;/p&gt;', '4', '2024-12-06 00:02:31', '2024-12-06 00:02:31'),
(12, 52, 'Two Hearts', '&lt;p&gt;Upon reaching Two Hearts with Reina, she will send the player a letter with the&amp;nbsp;&lt;a title=&quot;Quests&quot; href=&quot;https://fieldsofmistria.wiki.gg/wiki/Quests#Heart_Quests&quot;&gt;Heart Quest&lt;/a&gt;&amp;nbsp;for&amp;nbsp;&lt;strong&gt;Pie in the Sky&lt;/strong&gt;, asking the player to meet her at the&amp;nbsp;&lt;a title=&quot;Inn&quot; href=&quot;https://fieldsofmistria.wiki.gg/wiki/Inn&quot;&gt;Inn&lt;/a&gt;.&lt;/p&gt;\r\n&lt;blockquote&gt;\r\n&lt;p&gt;&lt;em&gt;[Player],&lt;/em&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;em&gt;You&#039;ve been in town too long without getting to taste test a new dish of mine. I think I&#039;ve got just the recipe to try out on a hardworking farmer like you.&lt;/em&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;em&gt;Come by the Inn when you get a chance!&lt;/em&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;em&gt;Reina&lt;/em&gt;&lt;/p&gt;\r\n&lt;/blockquote&gt;\r\n&lt;p&gt;Meeting Reina at the Inn will complete the Heart Quest.&lt;/p&gt;\r\n&lt;p&gt;Upon reaching Two Hearts with Reina, she will send the player a letter with the&amp;nbsp;Heart Quest&amp;nbsp;for&amp;nbsp;Pie in the Sky, asking the player to meet her at the&amp;nbsp;Inn.&lt;/p&gt;\r\n&lt;p&gt;[Player],&lt;/p&gt;\r\n&lt;p&gt;You&#039;ve been in town too long without getting to taste test a new dish of mine. I think I&#039;ve got just the recipe to try out on a hardworking farmer like you.&lt;/p&gt;\r\n&lt;p&gt;Come by the Inn when you get a chance!&lt;/p&gt;\r\n&lt;p&gt;Reina&lt;/p&gt;\r\n&lt;p&gt;Meeting Reina at the Inn will complete the Heart Quest.&lt;/p&gt;', '2', '2024-12-06 00:03:33', '2024-12-06 00:03:33'),
(13, 52, 'Four Hearts', '&lt;p&gt;Upon reaching Four Hearts with Reina, she will send the player a letter with the&amp;nbsp;&lt;a title=&quot;Quests&quot; href=&quot;https://fieldsofmistria.wiki.gg/wiki/Quests#Heart_Quests&quot;&gt;Heart Quest&lt;/a&gt;&amp;nbsp;for&amp;nbsp;&lt;strong&gt;Shopping Buddy&lt;/strong&gt;, asking the player to meet her at the&amp;nbsp;&lt;a title=&quot;General Store&quot; href=&quot;https://fieldsofmistria.wiki.gg/wiki/General_Store&quot;&gt;General Store&lt;/a&gt;.&lt;/p&gt;\r\n&lt;blockquote&gt;\r\n&lt;p&gt;&lt;em&gt;Hey [Player]!&lt;/em&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;em&gt;If you&#039;re free, I could use a shopping buddy! I want to pick out ingredients that&#039;ll really wow the judges.&lt;/em&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;em&gt;Meet me at the General Store when you&#039;ve got time!&lt;/em&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;em&gt;Reina&lt;/em&gt;&lt;/p&gt;\r\n&lt;/blockquote&gt;\r\n&lt;p&gt;Meeting Reina at the Inn will complete the Heart Quest.&lt;/p&gt;', '4', '2024-12-06 00:03:49', '2024-12-06 00:03:49');

